CREATE TABLE User(
  UserId varchar(255),
  Name nvarchar(255),
  Password varchar(200),
  PRIMARY KEY (UserId)
  );
  
CREATE TABLE Book(
  BookId int not null auto_increment,
  Title nvarchar(500),
  Author nvarchar(200),
  Price Decimal(10, 2),
  Description nvarchar(4000),
  ImageUrl varchar(255),
  PRIMARY KEY(BookId)
  );
  
